import React, { useEffect } from 'react';
import { connect, useDispatch, useSelector } from 'react-redux';
import { getParkingSpot } from '../reduxe/slices/parkingActions';
import axios from '../axios';
import { Post } from '../components/Post';

const Home = ({ parkingSpot, loading, error, getParkingSpot }) => {
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getParkingSpot()); // Виклик дії для отримання даних про парковочні місця
    axios.get('/update_parking_space'); // Запит на сервер за оновленням парковочних місць
  }, [dispatch]);

  return (
    <div>
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>Error: {error}</p>
      ) : (
        <>
          {parkingSpot.map((spot) => (
            <Post
              key={spot.id}
              id={spot.id}
              title={spot.title}
              imageUrl={spot.imageUrl}
              tags={spot.tags}
              isEditable={spot.isEditable}
            />
          ))}
        </>
      )}
    </div>
  );
};

const mapStateToProps = (state) => ({
  parkingSpot: selectParkingSpot(state), // Вибірка парковочних місць зі стану Redux
  loading: selectLoading(state), // Вибірка статусу завантаження зі стану Redux
  error: selectError(state), // Вибірка помилки зі стану Redux
});

const mapDispatchToProps = {
  getParkingSpot, // Підключення дії для отримання парковочних місць
};

export default connect(mapStateToProps, mapDispatchToProps)(Home);
