import {CreateSlice, createSlice} from '@reduxjs/toolkit';
import axios from '../../axios';

const initialState = {
    posts: {
        items: [],
        status: 'loading',
    },
    tags: {
        items: [],
        status: 'loading',
    },
};

const postsSlice = createSlice({
    name: 'posts',
    initialState,
    reducer: {},
});

export const postReducer = postsSlice.reducer;
