import axios from 'axios';

export const getParkingSpot = () => {
  return async (dispatch) => {
    try {
      const response = await axios.get('/update_parking_space');
      const data = response.data;

      dispatch({ type: 'GET_PARKING_SPOT_SUCCESS', payload: data });
    } catch (error) {
      dispatch({ type: 'GET_PARKING_SPOT_FAILURE', payload: error.message });
    }
  };
};

const initialState = {
    parkingSpot: null,
    loading: false,
    error: null,
  };
  
  const parkingReducer = (state = initialState, action) => {
    switch (action.type) {
      case 'GET_PARKING_SPOT_SUCCESS':
        return {
          ...state,
          parkingSpot: action.payload,
          loading: false,
          error: null,
        };
      case 'GET_PARKING_SPOT_FAILURE':
        return {
          ...state,
          parkingSpot: null,
          loading: false,
          error: action.payload,
        };
      default:
        return state;
    }
  };
  
    export default parkingReducer;
    export const selectParkingSpot = (state) => state.parking.parkingSpot;
    export const selectLoading = (state) => state.parking.loading;
    export const selectError = (state) => state.parking.error;
