import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { fetchSensorData } from '../../reduxe/slices/sensorActions';

export const SensorDataComponent = ({ sensorData, fetchSensorData }) => {
  useEffect(() => {
    console.log('fet ch func -> ', fetchSensorData);
    if (fetchSensorData)
    fetchSensorData();
  }, []);

  return (
    <div>
      <h1>Дані датчика:</h1>
      <ul>
        {sensorData?.map((data, index) => (
          <li key={index}>{data}</li>
        ))}
      </ul>
    </div>
  );
};

const mapStateToProps = (state) => ({
  sensorData: state.sensor.sensorData
});

export default connect(mapStateToProps, { fetchSensorData })(SensorDataComponent);
