import React, { useEffect } from 'react';
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Grid from '@mui/material/Grid';
import { useDispatch, useSelector, connect } from 'react-redux'
import { getParkingSpot, selectParkingSpot, selectLoading, selectError } from '../reduxe/slices/parkingActions';

import { Post } from '../components/Post';
import { TagsBlock } from '../components/TagsBlock';
import { CommentsBlock } from '../components/CommentsBlock';
import axios from '../axios';

export const Home = ({
  parkingSpot,
  loading,
  error,
  getParkingSpot,
}) => {
   const dispatch = useDispatch();
    useEffect(() =>{
    
    axios.get('/update_parking_space');
  }, [dispatch]);
  return (
    <div>
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>Error: {error}</p>
      ) : (
        <>
          {parkingSpot.map((spot) => (
            <Post
              key={spot.id}
              id={spot.id}
              title={spot.title}
              imageUrl={spot.imageUrl}
              tags={spot.tags}
              isEditable={spot.isEditable}
            />
          ))}
        </>
      )}
      {/* <Grid container spacing={4}>
        <Grid xs={12} item>
          {[...Array(5)].map(() => (
            <Post
              id={1}
              title="парковка"
              imageUrl="https://kievvlast.com.ua//project/resources/2022/01/OOKA3t2g.jpg"

              tags={[]}
              isEditable
            />
          ))}
        </Grid>
        <Grid xs={0} item>

        </Grid>
      </Grid> */}
    </div>
  );
};

const mapStateToProps = (state) => ({
  parkingSpot: selectParkingSpot(state), // Вибірка парковочних місць зі стану Redux
  loading: selectLoading(state), // Вибірка статусу завантаження зі стану Redux
  error: selectError(state), // Вибірка помилки зі стану Redux
});

const mapDispatchToProps = {
  getParkingSpot, // Підключення дії для отримання парковочних місць
};

export default connect(mapStateToProps, mapDispatchToProps)(Home);
